import { Component } from '@angular/core';
import { Store, select } from '@ngrx/store';
import { Observable } from 'rxjs';
import { savePlayers } from '../../State/compareActions';

@Component({
  selector: 'app-compare',
  templateUrl: './compare.component.html',
  styleUrls: ['./compare.component.scss'],
})
export class CompareComponent {
  players$: Observable<[]>;

  constructor(private store: Store<{ players: [], picks: [] }>) {
    this.players$ = store.pipe(select('players'));
  }

  savePlayers() {
    this.store.dispatch(savePlayers());
    console.log(this.store);
  }

}
<button id="addPlayers" (click)="savePlayers()">Players</button>
