import { createReducer, on } from '@ngrx/store';
import { addPlayers, addPicks} from '../State/compareActions';

export let initialState: any;

const _compareReducer = createReducer(initialState,
  on(addPlayers, (state) => [...state, ['Player 1', 'Player 2']]),
  on(addPicks, (state, action) => [...state, action.type]),
);

export function compareReducer(state: any, action: any) {
  return _compareReducer(state, action);
}
