import { Component, OnInit } from '@angular/core';
import { FplService } from './fpl.service';
import { LeagueStandings, Result } from 'app/interfaces/standings';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Bootstrap, Event, EventList, Teams } from 'app/interfaces/bootstrap';
import { Fixtures, FixturesList } from 'app/interfaces/fixtures';

@Component({
  selector: 'app-fpl',
  templateUrl: './fpl.component.html',
  styleUrls: ['./fpl.component.scss']
})
export class FplComponent implements OnInit {

  leagueForm!: FormGroup;
  standingsErrors: any;
  corsAnywhereURL: string = "https://cors-anywhere.herokuapp.com";
  league!: LeagueStandings;
  standings!: Result[];
  p: number = 1;

  bootstrap!: Bootstrap;
  events!: Event | any;
  teams!: Teams | any;
  gameweek!: EventList;
  gameweekID!: number;

  toogleDGWs: string = "Show";
  fixtureDGW!: number;
  fixtures!: FixturesList;
  hasMultiple: any;
  fixturesDGWFilter!: any;

  constructor(private _fplService: FplService,
    formBuilder: FormBuilder) {
      this.leagueForm = formBuilder.group({
        leagueID: [null, Validators.compose([Validators.required, Validators.pattern(/^[0-9]*$/)])],
      });
  }

  async ngOnInit() {

    (await this._fplService.getFplLeague())
    .subscribe(response => {
      this.league = response;
      this.standings = this.league.standings.results;
    }, error => {
      this.standingsErrors = error;
    },);


    (await this._fplService.getFplBootstrap())
    .subscribe(response => {
      this.bootstrap = response;
      this.events = this.bootstrap.events;
      this.teams = this.bootstrap.teams;
      console.log("TEAMS", this.teams);

      this.gameweek = this.events.filter((a: { [x: string]: boolean; }) => a['is_current'] === true);
      this.gameweekID = this.gameweek[0].id;
      sessionStorage.setItem('GameWeekID', JSON.stringify(this.gameweekID));
    });

  }

  async updateLeague() {

    (await this._fplService.updateFplLeague(this.league.league.id))
    .subscribe(response => {
      this.league = response;
      this.standings = this.league?.standings?.results;
    });

  }

  async toggleDoubleGws() {

    this.fixtureDGW = this.gameweekID + 1;
    this.toogleDGWs = this.toogleDGWs === "Show" ? "Hide" : "Show";

    (await this._fplService.getFixtures(this.fixtureDGW))
    .subscribe((response) => {
      this.fixtures = response;
      console.log("FIXTURES", this.fixtures);

      this.fixturesDGWFilter = this.fixtures.filter((set => f =>
        (!set.has(f.team_a) && !set.has(f.team_h)) && set.add(f.team_a))
        (new Set));
      console.log("FIXTURESDGWFILTER", this.fixturesDGWFilter);

    });

  }

}

<header>
  <h1>FPL</h1>
</header>

<section>
  <section>
    <p class="error" *ngIf="standingsErrors?.status === 403">
      Using
      <a href="https://cors-anywhere.herokuapp.com">
        CORS ANYWHERE
      </a>
      proxy that requires requesting temp access daily
    </p>

    <p class="error" *ngIf="standingsErrors?.status === 429">
      You are making too making requests
    </p>
  </section>
</section>

<aside>
  <button [(ngModel)]="toogleDGWs" class="button button-primary" (click)="toggleDoubleGws()"> {{ toogleDGWs }} double game weeks</button>

  <ng-container *ngIf="toogleDGWs == 'Hide'">
    <ul>
      <ng-container *ngFor="let team of teams">
        <ng-container *ngFor="let fixture of fixtures">
          <li *ngIf="fixture?.team_h === team?.id">
            <app-card>
              <p>{{ team?.name }}</p>
            </app-card>
          </li>
          <li *ngIf="fixture?.team_a === team?.id">
            <app-card>
              <p>{{ team?.name }}</p>
            </app-card>
          </li>
        </ng-container>
      </ng-container>
    </ul>
  </ng-container>
</aside>

<section>
  <form [formGroup]="leagueForm" *ngIf="this.leagueForm">
    <label>Switch League: </label>
    <input [(ngModel)]="league.league.id"
    (ngModelChange)="updateLeague()"
    class="form-control"
    formControlName="leagueID"/>
    <p class="error" *ngIf="leagueForm.controls['leagueID'].hasError('pattern') && (leagueForm.controls['leagueID'].dirty || leagueForm.controls['leagueID'].touched)">Use only numbers in League ID search</p>
  </form>
</section>

<section>
  <h2>League Name: {{ league.league.name }}</h2>

  <ul>
    <li *ngFor="let user of standings | paginate: { itemsPerPage: 5, currentPage: p }">
      <a routerLink="/team/{{ user?.entry }}">
        <app-card>
          <p> {{ user?.rank }} </p>
          <p> {{ user?.entry_name }} </p>
          <p> {{ user?.event_total }} </p>
          <p> {{ user?.total }} </p>
        </app-card>
      </a>
    </li>
  </ul>

  <pagination-controls (pageChange)="p = $event"></pagination-controls>
</section>
