import { Component } from '@angular/core';
import { Store, select } from '@ngrx/store';
import { Observable } from 'rxjs';
import { addPicks, addPlayers } from '../../State/compareActions';

@Component({
  selector: 'app-compare',
  templateUrl: './compare.component.html',
  styleUrls: ['./compare.component.scss'],
})
export class CompareComponent {
  teams$: Observable<[]>;

  constructor(private store: Store<{ teams: [] }>) {
    this.teams$ = store.pipe(select('teams'));
  }

  addPlayers() {
    this.store.dispatch(addPlayers());
  }
  addPicks() {
    this.store.dispatch(addPicks());
  }
}
<button id="addPlayers" (click)="addPlayers()">Players</button>
<button id="addPicks" (click)="addPicks()">Picks</button>

<div>Current Count: {{ teams$ | async }}</div>
