import { Component, OnInit } from '@angular/core';
import { FplService } from './fpl.service';
import { LeagueStandings, Result } from 'app/interfaces/standings';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Bootstrap, Event, EventList } from 'app/interfaces/bootstrap';
import { FixturesList } from 'app/interfaces/fixtures';

@Component({
  selector: 'app-fpl',
  templateUrl: './fpl.component.html',
  styleUrls: ['./fpl.component.scss']
})
export class FplComponent implements OnInit {

  leagueForm!: FormGroup;
  standingsErrors: any;
  corsAnywhereURL: string = "https://cors-anywhere.herokuapp.com";
  league!: LeagueStandings;
  standings: Result[] | any;
  p: number = 1;

  bootstrap!: Bootstrap;
  events!: Event | any;
  gameweek!: EventList;
  gameweekID!: number;

  toogleDGWs: string = "Show";
  fixtureDGW!: number;
  fixtures!: FixturesList;

  constructor(private _fplService: FplService,
    formBuilder: FormBuilder) {
      this.leagueForm = formBuilder.group({
        'league.league.id': [null, Validators.compose([Validators.required, Validators.pattern(/^[0-9]*$/)])],
      });
  }

  async ngOnInit() {

    (await this._fplService.getFplLeague())
    .subscribe(response => {
      this.league = response;
      this.standings = this.league.standings.results;
    }, error => {
      this.standingsErrors = error;
    },);


    (await this._fplService.getFplBootstrap())
    .subscribe(response => {
      this.bootstrap = response;
      this.events = this.bootstrap.events;

      this.gameweek = this.events.filter((a: { [x: string]: boolean; }) => a['is_current'] === true);
      this.gameweekID = this.gameweek[0].id;
      sessionStorage.setItem('GameWeekID', JSON.stringify(this.gameweekID));
    });

  }

  async updateLeague() {

    (await this._fplService.updateFplLeague(this.league.league.id))
    .subscribe(response => {
      this.league = response;
      this.standings = this.league?.standings?.results;
    });

  }

  async toggleDoubleGws() {
    this.fixtureDGW = 20;
    this.toogleDGWs = this.toogleDGWs == "Show" ? "Hide" : "Show";

    (await this._fplService.getFixtures(this.fixtureDGW))
    .subscribe((response) =>
      this.fixtures = response
    );
  }

}

<section>
  <h1>FPL</h1>

  <p class="error" *ngIf="standingsErrors?.status === 403">For temp access accept to proxied data
    <a href="https://cors-anywhere.herokuapp.com">
      {{ corsAnywhereURL }}
    </a>
  </p>
</section>

<section>
  <form [formGroup]="leagueForm" *ngIf="this.leagueForm">
    <label>Switch League: </label>
    <input [(ngModel)]="league.league.id"
    (ngModelChange)="updateLeague()"
    class="form-control"
    formControlName="league.league.id"/>
    <p class="error" *ngIf="leagueForm.controls['league?.league.id'].hasError('pattern') && (leagueForm.controls['league?.league.id'].dirty || leagueForm.controls['league?.league.id'].touched)">Use only numbers in League ID search</p>
  </form>
</section>

<section>
  <h2>League Name: {{ league.league.name }}</h2>

  <section>
    <button class="button button-primary" (click)="toggleDoubleGws()"> {{ toogleDGWs }} double game weeks</button>
    <ul>
      <li *ngFor="let fixture of fixtures">
        <p>{{ fixture.team_a }} - {{ fixture.team_h }}</p>
      </li>
    </ul>
  </section>


  <ul>
    <li *ngFor="let user of standings | paginate: { itemsPerPage: 5, currentPage: p }">
      <a routerLink="/team/{{ user?.entry }}">
        <app-card>
          <p> {{ user?.rank }} </p>
          <p> {{ user?.entry_name }} </p>
          <p> {{ user?.event_total }} </p>
          <p> {{ user?.total }} </p>
        </app-card>
      </a>
    </li>
  </ul>

  <pagination-controls (pageChange)="p = $event"></pagination-controls>
</section>
